import {
  Button,
  FormControl,
  FormLabel,
  Input,
  InputGroup,
  InputRightElement,
  VStack,
  useToast,
} from "@chakra-ui/react";
import { Flex, Spacer } from '@chakra-ui/react'
import React, { useState } from "react";
import axios from "axios";
import { Box , Container, Center} from '@chakra-ui/react';
import clicksound from './Sounds/Click-SoundEffect.mp3'


const Signup = () => {
  const [username, setName] = useState();
  const [email, setEmail] = useState();
  const [confirmpassword, setConfirmpassword] = useState();
  const [password, setPassword] = useState();
  const [show, setShow] = useState(false);
  const [showconfirm, setShowconfirm] = useState(false);
  const [loading, setLoading] = useState(false);
  const toast = useToast();

  const ding=(e)=>{
    const sound=new Audio(clicksound);
   sound.play();
  }
  

  const submitHandler = async () => {
    setLoading(true);
    if (!username || !email || !password || !confirmpassword) {
      toast({
        title: "Please fill all the required fields",
        status: "warning",
        duration: 5000,
        isClosable: "true",
        position: "bottom",
      });

      setLoading(false);
      return;
    }
    if (password !== confirmpassword) {
      toast({
        title: "Passwords Doesn't match",
        status: "warning",
        duration: 5000,
        isClosable: true,
        position: "top",
      });
      return;
    }
    try {
      const config = {
        headers: {
          "Content-type": "application/json",
        },
      };

      const { data } = await axios.post(
        '/roboWar',
        { username, email, password },
        config
      );

      toast({
        title: "Registration successful",
        duration: 5000,
        status: "success",
        isClosable: true,
        position: "top",
      });
    

      localStorage.setItem("userInfo", JSON.stringify(data));
      setLoading(false);
    } 
      catch (error) {
      toast({
        title: "Error Occured!",
        description: error.response.data.message,
        status: "error",
        duration: 5000,
        isClosable: true,
        position: "top",
      });
    }
  };

  // export default function Signup(){
  //   const [user,setuser]=useState({
  //     username :"",
  //     email:"",
  //     createpassword :"",
  //     confirmpassword:""
  //   })
  //   const handleClick=(e)=>{
  //     e.preventDefault();
  //     axios.post('/roboWar',user)
  //   }  

    return(
        <>
        <Container>
          <Flex color='white' align = "centre" >
            <Box w='5750px'>
            <VStack spacing="5px" color="white">
              
              <FormControl id="first-name" isRequired>
                <FormLabel>Username</FormLabel>
                <Input
                onClick={ding}
                placeholder="Enter your name"
                bg={username ? "gray.50" : "white"}
                color="black"
                _hover={{ borderColor: "black" }}
                borderColor="black"
                onChange={(event) => {
                  setName(event.target.value);
          }}
          value={username}
        />
      </FormControl>

      <FormControl id="email" isRequired>
        <FormLabel>Email</FormLabel>
        <Input
        onClick={ding}
          placeholder="Enter your email"
          bg={email ? "gray.50" : "white"}
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          value={email}
        />
      </FormControl>
      <FormControl id="password" isRequired>
        <FormLabel>Password</FormLabel>
        <Input
           onClick={ding}
          placeholder="Enter your password"
          bg={password ? "gray.50" : "white"}
          color="black"
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setPassword(event.target.value);
          }}
          value={password}
        />
         </FormControl>
         
         <FormControl id="confirm-password" isRequired>
          <FormLabel>Confirm Password</FormLabel>
          <InputGroup>
          <Input
          onClick={ding}
          placeholder="Enter your password"
          bg={confirmpassword ? "gray.50" : "white"}
          color="black"
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setConfirmpassword(event.target.value);
          }}
          value={confirmpassword}
        />
        </InputGroup>
      </FormControl>
      
      <Button
        colorScheme="teal"
        size='lg'
        margin={'5'}
        color='white'
        fontWeight='bold'
        borderRadius='md'
        bgGradient='linear(to-r, teal.500, green.500)'
        _hover={{
        bgGradient: 'linear(to-r, red.500, yellow.500)',
        }}
        style={{ marginTop: 15 }}
        onClick={submitHandler}
        isLoading={loading}
      >
        Sign Up
      </Button>
    </VStack> 
          

</Box>
  
  
  
  </Flex>
  </Container>
  </>
        )
    };
    
export default Signup;

