import React, { useRef, useEffect, useState } from "react";

function GamePage() {
  const canvasRef = useRef(null);
  const canvasWidth = window.innerWidth;
  const canvasHeight = window.innerHeight;

  const playerSize = {
    width: 100,
    height: 100,
  };

  const playerSpeed = 2;

  const [player1Position, setPlayer1Position] = useState({ x: 0, y: 500 });
  const [player2Position, setPlayer2Position] = useState({ x: 600, y: 500 });

  const [player1Direction, setPlayer1Direction] = useState({ x: 0, y: 0 });
  const [player2Direction, setPlayer2Direction] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");

    const handleKeyDown = (event) => {
      switch (event.key) {
        case "ArrowLeft":
          setPlayer2Direction((prevDirection) => ({
            ...prevDirection,
            x: -playerSpeed,
          }));
          break;
        case "ArrowRight":
          setPlayer2Direction((prevDirection) => ({
            ...prevDirection,
            x: playerSpeed,
          }));
          break;
        case "ArrowUp":
          setPlayer2Direction((prevDirection) => ({
            ...prevDirection,
            y: -playerSpeed,
          }));
          break;
        case "ArrowDown":
          setPlayer2Direction((prevDirection) => ({
            ...prevDirection,
            y: playerSpeed,
          }));
          break;
        case "a":
          setPlayer1Direction((prevDirection) => ({
            ...prevDirection,
            x: -playerSpeed,
          }));
          break;
        case "d":
          setPlayer1Direction((prevDirection) => ({
            ...prevDirection,
            x: playerSpeed,
          }));
          break;
        case "w":
          setPlayer1Direction((prevDirection) => ({
            ...prevDirection,
            y: -playerSpeed,
          }));
          break;
        case "s":
          setPlayer1Direction((prevDirection) => ({
            ...prevDirection,
            y: playerSpeed,
          }));
          break;
        default:
          break;
      }
    };

    const handleKeyUp = (event) => {
      switch (event.key) {
        case "ArrowLeft":
        case "ArrowRight":
        case "ArrowUp":
        case "ArrowDown":
          setPlayer2Direction({ x: 0, y: 0 });
          break;
        case "a":
        case "d":
        case "w":
        case "s":
          setPlayer1Direction({ x: 0, y: 0 });
          break;
        default:
          break;
      }
    };

    const updateGame = () => {
      context.clearRect(0, 0, canvas.width, canvas.height);
      context.fillStyle = "black";
      context.fillRect(0, 0, canvasWidth, canvasHeight);

      setPlayer1Position((prevPosition) => ({
        x: prevPosition.x + player1Direction.x,
        y: prevPosition.y + player1Direction.y,
      }));

      setPlayer2Position((prevPosition) => ({
        x: prevPosition.x + player2Direction.x,
        y: prevPosition.y + player2Direction.y,
      }));

      context.fillStyle = "red";
      context.fillRect(
        player1Position.x,
        player1Position.y,
        playerSize.width,
        playerSize.height
      );

      context.fillStyle = "blue";
      context.fillRect(
        player2Position.x,
        player2Position.y,
        playerSize.width,
        playerSize.height
      );

      requestAnimationFrame(updateGame);
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);

    updateGame();

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, [player1Position, player2Position, player1Direction, player2Direction]);

  return (
    <canvas ref={canvasRef} width={canvasWidth} height={canvasHeight}></canvas>
  );
}

export default GamePage;
