import React from 'react';
import Signup from'./Signup.jsx'
import Log from'./Log.jsx'
import { Tabs, TabList, TabPanels, Tab, TabPanel } from '@chakra-ui/react'
import { Container } from '@chakra-ui/react'
import { Button, ButtonGroup } from '@chakra-ui/react'
import { Text } from "@chakra-ui/react"
import clicksound from './Sounds/Click - Sound Effect.mp3'
import './App.css';

const ding=()=>{
  const sound = new Audio(clicksound);
  sound.play();
  }

export default function Login(){
  return(
    <div className="gradient-background">
    <div
          class = "image"
          style = {{
          
             height: "100vh",
             width: "100vw",
            //  backgroundImage: `url(${Playgroundback})`,
            //  backgroundImage:
            // 'url("https://th.bing.com/th/id/OIP.gXZ69lTKaHxWM4frZDPtAQHaEK?w=276&h=180&c=7&r=0&o=5&dpr=1.5&pid=1.7")',
            // backgroundImage: 'url("https://www.freepik.com/premium-photo/abstract-architectural-concrete-smooth-interior-minimalist-house-with-color-gradient-neon_42257376.htm#query=dark%20theme%20playground%20background&position=30&from_view=search&track=ais")',
            backgroundSize: "cover",
            backgroundRepeat: "no-repeat",
             Color : "white",
          }}
       >
      <div class="customText">
      <Text
      fontSize="6xl"
fontWeight="medium"
>ROBOWAR
</Text>
    <Tabs variant='enclosed' align='centre'>
      
    <Container>
    <TabList marginTop={'25'}>
    <ButtonGroup gap='2' margin={'5'}  >
        
        <Tab><Button colorScheme='teal' 
        as='button'
        p={4}
        color='white'
        fontWeight='bold'
        borderRadius='md'
        onClick={ding}
        >Sign Up</Button></Tab>


        
        <Tab><Button colorScheme='teal'
        as='button'
        p={4}
        color='white'
        fontWeight='bold'
        borderRadius='md'
        onClick={ding}
      >Log In</Button></Tab>
      
      </ButtonGroup>
      </TabList>
      </Container>   
      <TabPanels>
        <TabPanel>
        <Signup/>
        </TabPanel>
      <TabPanel>
        <Log/>
      </TabPanel>
      </TabPanels>
    </Tabs>
    </div>
  </div>
</div>
  );
}
