import React, { useState, Suspense } from 'react';
import { AbsoluteCenter, Container, Grid, Spacer, TagRightIcon } from '@chakra-ui/react'
import { Center, Square, Circle } from '@chakra-ui/react'
import { Button, ButtonGroup, Text } from '@chakra-ui/react'
import { Stack, HStack, VStack } from '@chakra-ui/react'
import { Avatar, AvatarBadge, AvatarGroup } from '@chakra-ui/react'
import { ArrowForwardIcon, BellIcon, ChevronRightIcon, EditIcon, EmailIcon, SettingsIcon } from '@chakra-ui/icons'
import { HamburgerIcon } from '@chakra-ui/icons'
import { Flex } from '@chakra-ui/react';
import { IconButton } from '@chakra-ui/react'
import { useToast } from '@chakra-ui/react'
import Setting from './Setting.jsx'
import { Switch,FormControl,FormLabel } from '@chakra-ui/react'
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuItemOption,
  MenuGroup,
  MenuOptionGroup,
  MenuDivider,
} from '@chakra-ui/react'
import Exit from './Exit.jsx'
import { useDisclosure } from '@chakra-ui/react'
import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
} from '@chakra-ui/react'
import { useNavigate } from 'react-router-dom';
import clicksound from './Sounds/Click - Sound Effect.mp3'

const ding=()=>{
  const sound = new Audio(clicksound);
  sound.play();
  }

// export default function Main(){
  const Main = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [placement, setPlacement] = React.useState('left');
  const [canvasVisible, setCanvasVisible] = useState(false);
  const navigate = useNavigate();

  const toggleCanvas = () => {
    setCanvasVisible(!canvasVisible);
  
   // Load and render the Canvas component when the "Play" button is clicked
   if (!canvasVisible) {
    import('./Play.jsx')
      .then((module) => {
        // Render the Canvas component inside a Suspense boundary
        setCanvasComponent(module.default);
      })
      .catch((error) => {
        console.error('Error loading the Canvas component:', error);
      });
  }
};

const [CanvasComponent, setCanvasComponent] = useState(null);

    return(
      <div className="gradient-background">
      <div
            class = "image"
            style = {{
            
               height: "100vh",
               width: "100vw",
              //  backgroundImage: `url(${Playgroundback})`,
              //  backgroundImage:
              // 'url("https://th.bing.com/th/id/OIP.gXZ69lTKaHxWM4frZDPtAQHaEK?w=276&h=180&c=7&r=0&o=5&dpr=1.5&pid=1.7")',
              // backgroundImage: 'url("https://www.freepik.com/premium-photo/abstract-architectural-concrete-smooth-interior-minimalist-house-with-color-gradient-neon_42257376.htm#query=dark%20theme%20playground%20background&position=30&from_view=search&track=ais")',
              backgroundSize: "cover",
              backgroundRepeat: "no-repeat",
               Color : "white",
            }}
         >
        <div class="customText">
        <Text
        fontSize="6xl"
  fontWeight="medium"
  >Main Menu
  </Text>
     <Grid margin={4} h={0} w={10} >
    
        <Menu>
  <MenuButton
    as={IconButton}
    textAlign={[ 'left', 'center' ]}    
    px={6}
    py={6}
    bg={'teal'}
    transition='all 0.2s'
    borderRadius='lg'
    borderWidth='1px'
    _hover={{ bg: 'teal.400' }}
    _expanded={{ bg: 'teal.400' }}
    _focus={{ boxShadow: 'outline' }}
    aria-label='Options'
   icon={<HamburgerIcon />}
    variant='outline'
    onClick={ding}
  />
  <MenuList>
    <MenuItem  >
       <AvatarGroup spacing='1rem' onClick={ding}>
          <Avatar bg='teal.500' onClick={onOpen}
        >

        </Avatar>
          
        </AvatarGroup> 
    </MenuItem>
    <MenuItem  onClick={ding}>
    Username 
    <Spacer/>
    <EditIcon/>
     
    </MenuItem>
    <MenuItem  onClick={ding}>
      Email
      <Spacer/>
      <EmailIcon/>
    </MenuItem>
    <MenuItem  onClick={ding}>
    Account Settings <Spacer/><SettingsIcon/>
    </MenuItem>
    <MenuItem  onClick={ding}>
      Notification
      <Spacer/>
      <Switch id='Notifications?' />
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
   
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  onClick={ding}>
      About Us
      <Spacer/>
      <ChevronRightIcon/>
    </MenuItem>

  </MenuList>
</Menu>
</Grid>

      {/* <div class="customText">
        <Text
        fontSize="6xl"
        fontWeight="medium"
      >ROBOWAR
      </Text>
      <Text
      fontSize="2xl"
      fontWeight="bold"
    >A Multiplayer Game
    </Text>
    </div> */}
    
        

<VStack direction='horizontal' spacing={6} align='center' margin={'50'}>
  <Button colorScheme='teal' 
  variant='solid' 
  width={100}
   
  // onClick={ding}
  onClick={() => {
    ding(); // Play sound effect
    toggleCanvas(); // Handle form submission
  }}
 
  >
    Play
        
  </Button>
  
  <Setting />
  <Exit />
</VStack>
<Stack direction='row' spacing={4} align='center' margin={'20px'}>
            <Button   
             colorScheme='red' variant='link' onClick={ding}>
              Share
            </Button>
            <Button colorScheme='blue' variant='button' onClick={ding}>
              Help
            </Button>
</Stack>


  {/* Render the Canvas component dynamically */}
  {canvasVisible && CanvasComponent && (
        <Suspense fallback={<div>Loading...</div>}>
          <CanvasComponent width={400} height={400} />
        </Suspense>
      )}
        
        
   </div>
   </div>
</div>
)
    }
  
    export default Main;
