import React, { useRef, useEffect, useState } from 'react';
import './Components.css'; // Import your component-specific CSS file

import spriteImage from './Images/background.png';
import kenjiIdle from './Images/kenji/Idle.png'; // Update the path to your character sprite
import samuraiIdle from './Images/samuraiMack/Idle.png';

const initialPlayerState = {
  position: {
    x: 0,
    y: 0
  },
  velocity: {
    x: 0,
    y: 0
  },
  imageSrc: kenjiIdle, // Use the correct path to your character sprite
  framesMax: 4, // Adjust this to match the number of frames in your sprite sheet
  scale: 2.5,
};

function CanvasDrawing() {
  const canvasRef = useRef(null);
  const [player, setPlayer] = useState(initialPlayerState);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const gravity = 0.7;

    // Set canvas dimensions
    canvas.width = 1024;
    canvas.height = 576;

    // Use the spriteImage as the background
    const backgroundImage = new Image();
    backgroundImage.src = spriteImage;

    // Load the player image
    const playerImage = new Image();
    playerImage.src = player.imageSrc;
    
    // Once the image is loaded, draw it on the canvas
    playerImage.onload = () => {
      const animationLoop = () => {
        // Update the player's position, frame, etc. based on your game logic
        // For now, let's just increment the player's x position
        player.position.x += 1;

        // Clear the canvas
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Draw the background image
        ctx.drawImage(backgroundImage, 0, 0, canvas.width, canvas.height);

        // Calculate the current frame
        const currentFrame = Math.floor(player.position.x / (canvas.width / player.framesMax));

        // Draw the player image
        ctx.drawImage(
          playerImage,
          currentFrame * (playerImage.width / player.framesMax),
          0,
          playerImage.width / player.framesMax,
          playerImage.height,
          player.position.x,
          player.position.y,
          playerImage.width / player.framesMax * player.scale,
          playerImage.height * player.scale
        );

        // Request the next animation frame
        requestAnimationFrame(animationLoop);
      };

      // Start the animation loop
      animationLoop();
    };

    // Cleanup (optional)
    return () => {
      // Remove event listeners or do other cleanup if needed
    };
  }, []); // Empty dependency array to run only once

  return (
    <div>
      <canvas ref={canvasRef}></canvas>
    </div>
  );
}

export default CanvasDrawing;
