import { Button, ButtonGroup } from '@chakra-ui/react'
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
} from '@chakra-ui/react'
import clicksound from './Sounds/Click - Sound Effect.mp3'

const ding=()=>{
  const sound = new Audio(clicksound);
  sound.play();
  }

export default function Exit(){
  const { isOpen, onOpen, onClose } = useDisclosure()
              return (
                <>
                  <Button colorScheme='teal' 
                  variant='solid' 
                  width={100} 
                  // onClick={onOpen}
                  onClick={() => {
                    ding(); // Play sound effect
                    onOpen(); // Handle form submission
                  }}
                  >Exit</Button>
            
                  <Modal isOpen={isOpen} onClose={onClose}>
                    <ModalOverlay />
                    <ModalContent>
                      <ModalHeader>Exit</ModalHeader>
                      <ModalCloseButton />
                      <ModalBody>
                        Do you want to Exit?
                      </ModalBody>
            
                      <ModalFooter>
                        <Button colorScheme='blue' mr={3} onClick={onClose}>
                          Yes
                        </Button>
                        <Button colorScheme='blue' mr={3} onClick={ding}>
                        No
                        </Button>
                      </ModalFooter>
                        </ModalContent>
                  </Modal>
                </>
              )
            
          }
