import { Center, Square, Circle } from '@chakra-ui/react'
import { Button, ButtonGroup, Text } from '@chakra-ui/react'
import { Stack, HStack, VStack } from '@chakra-ui/react'
import { Avatar, AvatarBadge, AvatarGroup } from '@chakra-ui/react'
import { ArrowForwardIcon, BellIcon, ChevronRightIcon, EditIcon, EmailIcon, SettingsIcon } from '@chakra-ui/icons'
import { HamburgerIcon } from '@chakra-ui/icons'
import { Flex } from '@chakra-ui/react';
import { IconButton } from '@chakra-ui/react'
import { useToast } from '@chakra-ui/react'
import Setting from './Setting.jsx'
import Home from "./Home.jpg"
import Play from "./Play.jsx"

import { Switch,FormControl,FormLabel } from '@chakra-ui/react'
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuItemOption,
  MenuGroup,
  MenuOptionGroup,
  MenuDivider,
} from '@chakra-ui/react'
import Exit from './Exit.jsx'
import { useDisclosure } from '@chakra-ui/react'
// import { handleClick, size } from '@chakra-ui/react'
import { useNavigate } from 'react-router-dom';


import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
} from '@chakra-ui/react'

const ding=()=>{
  const sound=new Audio("https://pixabay.com/music/electro-cyber-war-126419/");
  sound.play();
  }

export default function Main(){
  const { isOpen, onOpen, onClose } = useDisclosure()
  const [placement, setPlacement] = React.useState('left')
  const navigate = useNavigate();
   

  const navigateToPlay = () => {
    navigate('/Play');
  };
    return(
    <>
     <Grid margin={4} h={0} w={10} >
    
        <Menu>
  <MenuButton
    as={IconButton}
    textAlign={[ 'left', 'center' ]}    
    px={6}
    py={6}
    bg={'teal'}
    transition='all 0.2s'
    borderRadius='lg'
    borderWidth='1px'
    _hover={{ bg: 'teal.400' }}
    _expanded={{ bg: 'teal.400' }}
    _focus={{ boxShadow: 'outline' }}
    aria-label='Options'
   icon={<HamburgerIcon />}
    variant='outline'
  />
  <MenuList>
    <MenuItem  >
       <AvatarGroup spacing='1rem' onClick={ding}>
          <Avatar bg='teal.500' onClick={onOpen}
        >

        </Avatar>
          
        </AvatarGroup> 
    </MenuItem>
    <MenuItem  >
    Username 
    <Spacer/>
    <EditIcon/>
     
    </MenuItem>
    <MenuItem  >
      Email
      <Spacer/>
      <EmailIcon/>
    </MenuItem>
    <MenuItem  >
    Account Settings <Spacer/><SettingsIcon/>
    </MenuItem>
    <MenuItem  >
      Notification
      <Spacer/>
      <Switch id='Notifications?' />
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      
    </MenuItem>
   
    <MenuItem  >
      
    </MenuItem>
    <MenuItem  >
      About Us
      <Spacer/>
      <ChevronRightIcon/>
    </MenuItem>

  </MenuList>
</Menu>
</Grid>

      <div class="customText">
        <Text
        fontSize="6xl"
        fontWeight="medium"
      >ROBOWAR
      </Text>
      {/* <Text
      fontSize="2xl"
      fontWeight="bold"
    >A Multiplayer Game
    </Text> */}
    </div>
    
        

<VStack direction='horizontal' spacing={6} align='center' margin={'50'}>
  <Button colorScheme='teal' variant='solid' width={150} onClick={ding}
  onClick={navigateToPlay}>
  Play
        
  </Button>
  {/* <Playground/> */}
  <Setting />
  <Exit />
</VStack>
<Stack direction='row' spacing={4} align='center' margin={'20px'}>
            <Button   
             colorScheme='red' variant='link' onClick={ding}>
              Share
            </Button>
            <Button colorScheme='blue' variant='button' onClick={ding}>
              Help
            </Button>
</Stack>



        
        
   </>
)
    }



