import React from 'react';

const AttackHitbox = ({ position, size }) => {
  const style = {
    position: 'absolute',
    left: position.x,
    top: position,
    width: size,
    height: size,
    background: 'red', // You can style it as you like
    opacity: 0.5, // Adjust the transparency
  };

  return <div style={style} />;
};

export default AttackHitbox;
