import React, { useRef, useEffect, useState } from 'react';
import './Components.css';

import spriteImage from './Images/background.png';
import { AnimationFrame } from './AnimationFrame.js';
import kenjiImage from './Idle3 .png';
import samuraiImage from './fighter1.png';

const gravity = 0.7;
const initialPlayerState = {
  player: {
    position: {
      x: 100,
      y: 400
    },
    velocity: {
      x: 10,
      y: 0
    },
    scale: 2.5,
    health: 100,
    isAttacking: false,
    // attack: function() {
    //   // Add your attack logic for the player here
    //   // Example: Deal damage to the enemy
    //   setPlayers.enemy.health -= 10;
    // }
  },
  enemy: {
    position: {
      x: 250,
      y: 400
    },
    velocity: {
      x: 10,
      y: 0
    },
    scale: 2.5,
    health: 100,
    isAttacking: false,
    // attack: function() {
    //   // Add your attack logic for the enemy here
    //   // Example: Deal damage to the player
    //   setPlayers.player.health -= 10;
    // }
  }
};

const kenjiFrameWidth = 284;
const kenjiFrameHeight = 600;
const kenjiTotalFrames = 1;
const kenjiAnimationSpeed = 10;

const samuraiFrameWidth = 284;
const samuraiFrameHeight = 600;
const samuraiTotalFrames = 1;
const samuraiAnimationSpeed = 10;

function CanvasDrawing() {
  const canvasRef = useRef(null);
  const [players, setPlayers] = useState(initialPlayerState);
  const [currentFrame, setCurrentFrame] = useState(0);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = 1024;
    canvas.height = 576;

    const backgroundImage = new Image();
    backgroundImage.src = spriteImage;

    const image = new Image();
    image.src = kenjiImage;

    image.onload = function () {
      ctx.drawImage(image, 50, 50);
    };

    const fighter = new Image();
    fighter.src = samuraiImage;

    fighter.onload = function () {
      ctx.drawImage(fighter, 50, 50);
    };

    const kenjiAnimationFrame = new AnimationFrame({
      image: image,
      frameWidth: kenjiFrameWidth,
      frameHeight: kenjiFrameHeight,
      totalFrames: kenjiTotalFrames,
      animationSpeed: kenjiAnimationSpeed,
    });

    const samuraiAnimationFrame = new AnimationFrame({
      image: fighter,
      frameWidth: samuraiFrameWidth,
      frameHeight: samuraiFrameHeight,
      totalFrames: samuraiTotalFrames,
      animationSpeed: samuraiAnimationSpeed,
    });

    const keys = {
      a: false,
      d: false,
      w: false,
      space: false,
      ArrowLeft: false,
      ArrowRight: false,
      ArrowUp: false,
      ArrowDown: false,
    };

    const handleKeyDown = (event) => {
      if (!players.player.dead) {
        switch (event.key) {
          case 'a':
            keys.a = true;
            break;
          case 'd':
            keys.d = true;
            break;
          case 'w':
            players.player.velocity.y = -20;
            break;
          case ' ':
            // players.player.attack();
            break;
        }
      }

      if (!players.enemy.dead) {
        switch (event.key) {
          case 'ArrowLeft':
            keys.ArrowLeft = true;
            break;
          case 'ArrowRight':
            keys.ArrowRight = true;
            break;
          case 'ArrowUp':
            players.enemy.velocity.y = -20;
            break;
          case 'ArrowDown':
            players.enemy.attack();
            break;
        }
      }
    };

    const handleKeyUp = (event) => {
      switch (event.key) {
        case 'a':
          keys.a = false;
          break;
        case 'd':
          keys.d = false;
          break;
      }

      // enemy keys
      switch (event.key) {
        case 'ArrowLeft':
          keys.ArrowLeft = false;
          break;
        case 'ArrowRight':
          keys.ArrowRight = false;
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    
    const attack = (attacker, target) => {
      if (!attacker.isAttacking && !target.dead) {
        attacker.isAttacking = true;
        // Add your attack logic here
    
        // Example: Deal damage to the target
        setPlayers((prevPlayers) => {
          const updatedPlayers = { ...prevPlayers };
          if (attacker === prevPlayers.player) {
            updatedPlayers.enemy.health -= 10;
          } else {
            updatedPlayers.player.health -= 10;
          }
          return updatedPlayers;
        });
      }
    };
    
    

    const animate = () => {
      window.requestAnimationFrame(animate);
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(backgroundImage, 0, 0, canvas.width, canvas.height);

      if (keys.a) {
        players.player.position.x -= players.player.velocity.x;
      } else if (keys.d) {
        players.player.position.x += players.player.velocity.x;
      } else if (keys.w) {
        // Handle jump for player
        // Add your jump logic here
      }

      if (keys.ArrowLeft) {
        players.enemy.position.x -= players.enemy.velocity.x;
      } else if (keys.ArrowRight) {
