import React, { useRef, useEffect, useState } from 'react';
import './Components.css';

import spriteImage from './Images/background.png';
import { AnimationFrame } from './AnimationFrame.js';
import kenjiImage from './Idle3 .png';
import samuraiImage from './fighter1.png';

const gravity = 0.7;
const initialPlayer1State = {
  position: {
    x: 100,
    y: 400
  },
  velocity: {
    x: 10,
    y: 0
  },
  scale: 2.5,
  health: 100 
};

const initialPlayer2State = {
  position: {
    x: 250,
    y: 400
  },
  velocity: {
    x: 10,
    y: 0
  },
  scale: 2.5,
  health: 100 
};

const kenjiFrameWidth = 284;
const kenjiFrameHeight = 600;
const kenjiTotalFrames = 1;
const kenjiAnimationSpeed = 10;

const samuraiFrameWidth = 284;
const samuraiFrameHeight = 600;
const samuraiTotalFrames = 1;
const samuraiAnimationSpeed = 10;

function CanvasDrawing() {
  const canvasRef = useRef(null);
  const [player1, setPlayer1] = useState(initialPlayer1State);
  const [player2, setPlayer2] = useState(initialPlayer2State);
  const [currentFrame, setCurrentFrame] = useState(0);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = 1024;
    canvas.height = 576;

    const backgroundImage = new Image();
    backgroundImage.src = spriteImage;

    const image = new Image();
    image.src = kenjiImage;

    image.onload = function () {
      ctx.drawImage(image, 50, 50);
    };

    const fighter = new Image();
    fighter.src = samuraiImage;

    fighter.onload = function () {
      ctx.drawImage(fighter, 50, 50);
    };

    const kenjiAnimationFrame = new AnimationFrame({
      image: image,
      frameWidth: kenjiFrameWidth,
      frameHeight: kenjiFrameHeight,
      totalFrames: kenjiTotalFrames,
      animationSpeed: kenjiAnimationSpeed,
    });

    const samuraiAnimationFrame = new AnimationFrame({
      image: fighter,
      frameWidth: samuraiFrameWidth,
      frameHeight: samuraiFrameHeight,
      totalFrames: samuraiTotalFrames,
      animationSpeed: samuraiAnimationSpeed,
    });

    const keys = {
      a: false,
      d: false,
      w: false,
      space: false,
      ArrowLeft: false,
      ArrowRight: false,
      ArrowUp: false,
      ArrowDown: false,
    };

    const handleKeyDown = (event) => {
      if (event.key === 'a') {
        keys.a = true;
      } else if (event.key === 'd') {
        keys.d = true;
      } else if (event.key === 'w') {
        keys.w = true;
      } else if (event.key === ' ') {
        keys.space = true;
      } else if (event.key === 'ArrowLeft') {
        keys.ArrowLeft = true;
      } else if (event.key === 'ArrowRight') {
        keys.ArrowRight = true;
      } else if (event.key === 'ArrowUp') {
        keys.ArrowUp = true;
      } else if (event.key === 'ArrowDown') {
        keys.ArrowDown = true;
      }
    };

    const handleKeyUp = (event) => {
      if (event.key === 'a') {
        keys.a = false;
      } else if (event.key === 'd') {
        keys.d = false;
      } else if (event.key === 'w') {
        keys.w = false;
      } else if (event.key === ' ') {
        keys.space = false;
      } else if (event.key === 'ArrowLeft') {
        keys.ArrowLeft = false;
      } else if (event.key === 'ArrowRight') {
        keys.ArrowRight = false;
      } else if (event.key === 'ArrowUp') {
        keys.ArrowUp = false;
      } else if (event.key === 'ArrowDown') {
        keys.ArrowDown = false;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(backgroundImage, 0, 0, canvas.width, canvas.height);

      if (keys.a) {
        player1.position.x -= player1.velocity.x;
      } else if (keys.d) {
        player1.position.x += player1.velocity.x;
      } else if (keys.w) {
        // Handle jump for player 1
        // Add your jump logic here
      } else if (keys.space) {
        // Handle attack for player 1
        // Add your attack logic here
      }

      if (keys.ArrowLeft) {
        player2.position.x -= player2.velocity.x;
      } else if (keys.ArrowRight) {
        player2.position.x += player2.velocity.x;
      } else if (keys.ArrowUp) {
        // Handle jump for player 2
        // Add your jump logic here
      } else if (keys.ArrowDown) {
        // Handle attack for player 2
        // Add your attack logic here
      }

      kenjiAnimationFrame.draw(ctx, player1.position.x, player1.position.y);
      samuraiAnimationFrame.draw(ctx, player2.position.x + 400, player2.position.y);

      setCurrentFrame((prevFrame) => prevFrame + 1);

      requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);

  return (
    <div>
      <canvas ref={canvasRef}></canvas>
    </div>
  );
}

export default CanvasDrawing;
