import React from 'react';
import { Container ,Box, Text } from '@chakra-ui/react'
import { Stack, Button, VStack } from '@chakra-ui/react'
import { Avatar, AvatarGroup } from '@chakra-ui/react'
import { BellIcon } from '@chakra-ui/icons'
import Setting from './Setting.jsx'
import Exit from './Exit.jsx'
import { useDisclosure } from '@chakra-ui/react'
// import { handleClick, size } from '@chakra-ui/react'
import {  Collapse } from '@chakra-ui/react'
// import {
//   Drawer,
//   DrawerBody,
//   DrawerFooter,
//   DrawerHeader,
//   DrawerOverlay,
//   DrawerContent,
//   DrawerCloseButton,
// } from '@chakra-ui/react'


const ding=()=>{
  const sound=new Audio("https://www.soundjay.com/misc/sounds/small-bell-ring-01a.mp3");
  sound.play();
  }


export default function Main(){
  const { isOpen, onOpen,onClose,onToggle } = useDisclosure()
 

  return (
    <div className = "gradient-background">
    <div
          class = "image"
          style = {{
          
             height: "100vh",
             width: "100vw",
            //  backgroundImage: `url(${Playgroundback})`,
            //  backgroundImage:
            // 'url("https://th.bing.com/th/id/OIP.gXZ69lTKaHxWM4frZDPtAQHaEK?w=276&h=180&c=7&r=0&o=5&dpr=1.5&pid=1.7")',
            // backgroundImage: 'url("https://www.freepik.com/premium-photo/abstract-architectural-concrete-smooth-interior-minimalist-house-with-color-gradient-neon_42257376.htm#query=dark%20theme%20playground%20background&position=30&from_view=search&track=ais")',
            backgroundSize: "cover",
            backgroundRepeat: "no-repeat",
             Color : "white",
          }}
       >
      <div class="customText">
      <Text
      fontSize="6xl"
fontWeight="medium"
>Main Menu
</Text>
    <AvatarGroup spacing='1rem' onClick={ding}>
          <Avatar bg='teal.500' onClick={onToggle}
       >

       </Avatar>
          
        </AvatarGroup>
      <Collapse in={isOpen} animateOpacity>
        <Box
        margin='0px'
        w='60px'
          p='40px'
          color='white'
          mt='4'
          bg='teal.500'
          rounded='md'
          shadow='md'
        >
         Meenal
        </Box>
      </Collapse>
      <BellIcon></BellIcon>
  
  


        <Container>

<VStack direction='horizontal' spacing={6} align='center' margin={'50'}>
  <Button colorScheme='teal' variant='solid' width={100} onClick={ding}>
    Play
  </Button>
  
  <Setting />
  <Exit />
</VStack>
<Stack direction='row' spacing={4} align='center' margin={'20px'}>
            <Button  colorScheme='red' variant='link' onClick={ding}>
              Share
            </Button>
            <Button colorScheme='blue' variant='outline' onClick={ding}>
              Help
            </Button>
</Stack>


</Container>
        
</div>
</div>
        </div>
   ) 
  }
