})

const handleClick = (e) => {
  e.preventDefault();

  const headers = {
    'Body':'user',
    'Content-Type': 'application/json',
    'Authorization': 'Bearer YourAccessToken',
  };

  axios.post('/roboWar',{ headers })
    .then(response => {
      console.log('Response:', response.data);
    })
    .catch(error => {
      console.error('Error:', error);
    });
}
  
