import axios from 'axios';
import React, { useState } from 'react';
import { Box, Button, ButtonGroup, Stack } from '@chakra-ui/react'
import { Flex, Spacer } from '@chakra-ui/react'
import { Container } from '@chakra-ui/react'
import { VisuallyHidden, VisuallyHiddenInput } from '@chakra-ui/react'
import {CheckIcon} from '@chakra-ui/icons'
import {
    FormControl,
    FormLabel,
    FormErrorMessage,
    FormHelperText,
    Input,
  } from '@chakra-ui/react'

  const ding=(e)=>{
     const sound=new Audio("https://www.soundjay.com/misc/sounds/small-bell-ring-01a.mp3");
    sound.play();
   }

  export default function Log(){
    const [password, setPassword] = useState();
    const [show, setShow] = useState(false);
    const [username, setName] = useState();
    const [loading, setLoading] = useState(false);
    const toast = useToast();
    // const history = useHistory();
    

  const handleClick = () => setShow(!show);
  const submitHandler = async () => {
    setLoading(true);
    if(!username || !password) {
      toast({
        title:"Please fill all the fields",
        status:"warning",
        duration: 5000,
        isClosable: true,
        position: "top",
      });
      setLoading(false);
      return;
    }

    try{
      const config = {
        headers: {
          "Content-type" : "application/json",
        },
      };
      const {data} = await axios.post(
        "/api/user/login",
        { username, password },
        config
      );

      toast({
        title: "Login Successful",
        status:"success",
        duration:5000,
        isClosable: true,
        position: "top"
      });
      localStorage.setItem("userInfo",JSON.stringify(data));
      setLoading(false);
    } catch(err) {
        toast({
          title: "Error Occured!",
          description: err.response.data.message,
          status: "error",
          duration: 5000,
          isClosable: true,
          position: "top"

        });
        setLoading(false);
    }
  };

    return(
        <>
        <Container>
        <Flex color='white'>
        <Box w='5750px'>
        <FormControl isRequired>
            {/* <FormLabel>Username</FormLabel>
            <Input placeholder='Username' value={user.username} onChange={(event)=>setuser({...user,username:event.target.value})}/> */}
            <FormLabel>Username</FormLabel>
        <Input
          placeholder="Enter your Username"
          bg={username ? "gray.50" : "white"}
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setName(event.target.value);
          }}
          value={username}
        />
            
            
            
            {/* <FormLabel>Password</FormLabel>
            <Input placeholder='Password' value={user.password} onChange={(event)=>setuser({...user,password:event.target.value})}/> */}
            

            
            

            
        </FormControl>
        </Box>
        </Flex>
        
        <Button colorScheme='teal'
        size='lg'
        margin={'5'}
        onClick={handleClick}
        >Login</Button>

        <Stack direction='row' spacing={4} align='center' margin={'20px'}>
            <Button  colorScheme='red' variant='link' >
      Forgot Password?
            </Button>
            <Button colorScheme='blue' variant='outline'>
              Help
            </Button>
        </Stack>
        </Container>
        </>
        )
    };
