import axios from 'axios';
import React, { useState } from 'react';
import { Box, Button, ButtonGroup, Stack,useToast,VStack } from '@chakra-ui/react'
import { Flex, Spacer } from '@chakra-ui/react'
import { Container } from '@chakra-ui/react'
import {
    FormControl,
    FormLabel,
    FormErrorMessage,
    FormHelperText,
    Input,
  } from '@chakra-ui/react'
  

  // const ding=(e)=>{
  //   //  const sound=new Audio("https://www.soundjay.com/misc/sounds/small-bell-ring-01a.mp3");
  //   const sound=new Audio('./Sounds/Click - Sound Effect.mp3');
  //   sound.play();
  //  }

  export default function Log(){
    const [password, setPassword] = useState();
    const [show, setShow] = useState(false);
    const [username, setName] = useState();
    const [loading, setLoading] = useState(false);
    const toast = useToast();
    

  const handleClick = () => setShow(!show);
  const submitHandler = async () => {
    setLoading(true);
    if(!username || !password) {
      toast({
        title:"Please fill all the fields",
        status:"warning",
        duration: 5000,
        isClosable: true,
        position: "top",
      });
      setLoading(false);
      return;
    }

    try{
      const config = {
        headers: {
          "Content-type" : "application/json",
        },
      };
      const {data} = await axios.post(
        "/api/user/login",
        { username, password },
        config
      );

      toast({
        title: "Login Successful",
        status:"success",
        duration:5000,
        isClosable: true,
        position: "top"
      });
      localStorage.setItem("userInfo",JSON.stringify(data));
      setLoading(false);
    } catch(err) {
        toast({
          title: "Error Occured!",
          description: err.response.data.message,
          status: "error",
          duration: 5000,
          isClosable: true,
          position: "top"

        });
        setLoading(false);
    }
  };

    return(
        <>
        <Container>
        <Flex color='white'>
        <Box w='5750px'>
        <FormControl isRequired>
            <FormLabel>Username</FormLabel>
        <Input
          placeholder="Enter your name"
          bg={username ? "gray.50" : "white"}
          color="black"
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setName(event.target.value);
          }}
          value={username}
        />
        </FormControl>

        <FormControl id="password" isRequired>
        <FormLabel>Password</FormLabel>
        <Input
          placeholder="Enter your password"
          bg={password ? "gray.50" : "white"}
          color="black"
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setPassword(event.target.value);
          }}
          value={password}
        />
        </FormControl>
        </Box>
        </Flex>
        
        <Button colorScheme="teal"
        size='lg'
        margin={'5'}
        color='white'
        fontWeight='bold'
        borderRadius='md'
        bgGradient='linear(to-r, teal.500, green.500)'
        _hover={{
        bgGradient: 'linear(to-r, red.500, yellow.500)',
        }}
        style={{ marginTop: 15 }}
        onClick={submitHandler}
        isLoading={loading}
        >Login</Button>

        <Stack direction='row' spacing={4} align='center' margin={'20px'}>
            <Button fontWeight={'bold'} colorScheme='red' variant='link' >
      Forgot Password?
            </Button>
            <Button fontWeight={'bold'} colorScheme='blue' variant='outline'>
              Help
            </Button>
        </Stack>
        </Container>
        </>
        )
    };
