import React, { useState } from "react";
import axios from "axios";
import {
  Button,
  FormControl,
  FormLabel,
  Input,
  InputGroup,
  InputRightElement,
  VStack,
  useToast,
  Box,
  Container,
  Flex,
 
} from "@chakra-ui/react";
import { ViewIcon, ViewOffIcon } from "@chakra-ui/icons";
import { useNavigate } from 'react-router-dom';

import clicksound from './Sounds/Click - Sound Effect.mp3'


const Signup = () => {
  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [confirmpassword, setConfirmpassword] = useState();
  const [password, setPassword] = useState();
  const [show, setShow] = useState(false);
  const [showconfirm, setShowconfirm] = useState(false);
  const [loading, setLoading] = useState(false);
  const toast = useToast();
  const navigate = useNavigate();


  const ding = () => {
    const sound = new Audio(clicksound);
    sound.play();
  };
  const handleClick = () => setShow(!show);
  const handleConfirmClick = () => {
    setShow(!show);
  };
  const submitHandler = async () => {
    setLoading(true);
    if (!name || !email || !password || !confirmpassword) {
      toast({
        title: "Please fill all the required fields",
        status: "warning",
        duration: 5000,
        isClosable: "true",
        position: "bottom",
      });

      setLoading(false);
      return;
    }
    if (password !== confirmpassword) {
      toast({
        title: "Passwords Doesn't match",
        status: "warning",
        duration: 5000,
        isClosable: true,
        position: "top",
      });
      setLoading(false);
      return;
    }
    try {
      const config = {
        headers: {
          "Content-type": "application/json",
        },
      };

      const { data } = await axios.post(
        "http://127.0.0.1:5000/roboWar/auth",
        { name, email, password},
        config
      );

      toast({
        title: "Registration successful",
        duration: 5000,
        status: "success",
        isClosable: true,
        position: "top",
      });

  //     localStorage.setItem("userInfo", JSON.stringify(data));
  //     setLoading(false);
  //     navigate('/Main')

  //   } catch (error) {
  //     toast({
  //       title: "Error Occured!",
  //       description: error.data.message,
  //       status: "error",
  //       duration: 5000,
  //       isClosable: true,
  //       position: "top",
  //     });
  //   }    
  // };

  localStorage.setItem("userInfo", JSON.stringify(data));
  setLoading(false);
  navigate('/Main');
} catch (error) {
  if (error.response && error.response.data && error.response.data.message) {
    // Handle the case where there's an error message in the response
    toast({
      title: "Error Occurred!",
      description: error.response.data.message, // Access error message from response
      status: "error",
      duration: 5000,
      isClosable: true,
      position: "top",
    });
  } else {
    // Handle other errors
    toast({
      title: "Error Occurred!",
      description: "An error occurred while processing your request.",
      status: "error",
      duration: 5000,
      isClosable: true,
      position: "top",
    });
  }
}
};

  return (
    <>
      <Container>
        <Flex color="black" align="center">
          <Box w="5750px">
            <VStack spacing="5px" color="black">
              <FormControl id="first-name" isRequired>
                <FormLabel>Username</FormLabel>
                <Input
                onClick={ding}
                placeholder="Enter your name"
                bg={name ? "gray.50" : "white"}
                color="black"
                _hover={{ borderColor: "black" }}
                borderColor="black"
                onChange={(event) => {
                  setName(event.target.value);
          }}
          value={name}
        />
      </FormControl>

      <FormControl id="email" isRequired>
        <FormLabel>Email</FormLabel>
        <Input
        onClick={ding}
        placeholder="Enter your email"
          bg={email ? "gray.50" : "white"}
          _hover={{ borderColor: "black" }}
          color="black"
          borderColor="black"
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          value={email}
        />
      </FormControl>
      <FormControl id="password" isRequired>
        <FormLabel>Password</FormLabel>
        <InputGroup>
        <Input
           onClick={ding}
          placeholder="Enter your password"
          bg={password ? "gray.50" : "white"}
          color="black"
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setPassword(event.target.value);
          }}
          value={password}
        />
       <InputRightElement w="4.5em">
            <Button
              h="1.75em"
              size="md"
              onClick={handleClick}
              color="black"
              variant="ghost"
            >
              {show ? <ViewOffIcon /> : <ViewIcon />}
            </Button>
          </InputRightElement>
          </InputGroup>
         </FormControl>
         
         <FormControl id="confirm-password" isRequired>
          <FormLabel>Confirm Password</FormLabel>
          <InputGroup>
          <Input
          onClick={ding}
          type={show ? "text" : "password"}
          placeholder="Re-enter your password"
          bg={confirmpassword ? "gray.50" : "white"}
          color="black"
          _hover={{ borderColor: "black" }}
          borderColor="black"
          onChange={(event) => {
            setConfirmpassword(event.target.value);
          }}
          value={confirmpassword}
        />
        <InputRightElement w="4.5em">
            <Button
              h="1.75em"
              size="md"
              onClick={handleConfirmClick}
              color="black"
              variant="ghost"
            >
              {showconfirm ? <ViewOffIcon /> : <ViewIcon />}
            </Button>
          </InputRightElement>
        </InputGroup>
      </FormControl>
              <Button
                colorScheme="teal"
                size="lg"
                margin="5"
                color="white"
                fontWeight="bold"
                borderRadius="md"
                bgGradient="linear(to-r, teal.500, green.500)"
                _hover={{
                  bgGradient: "linear(to-r, red.500, yellow.500)",
                }}
                style={{ marginTop: 15 }}
                onClick={() => {
                  ding(); // Play sound effect
                  submitHandler(); // Handle form submission
                }}
                isLoading={loading}
              >
                Sign Up
              </Button>
            </VStack>
          </Box>
        </Flex>
      </Container>
    </>
  );
};

export default Signup;
