// Exit.jsx
import React from 'react';
import { Button } from '@chakra-ui/react';
import { useDisclosure } from '@chakra-ui/react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const Exit = () => {
  const navigate = useNavigate(); // Get the navigate function

  const { isOpen, onOpen, onClose } = useDisclosure();

  const ding = () => {
    // Your ding function
    const ding=()=>{
        const sound = new Audio(clicksound);
        sound.play();
        }
  };

  return (
    <>
      <Button
        colorScheme="teal"
        variant="solid"
        width={100}
        onClick={() => {
          ding();
          onOpen();
        }}
      >
        Exit
      </Button>

      {/* Rest of your modal code */}
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Exit</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            Do you want to Exit?
          </ModalBody>
            
      <ModalFooter>
        <Button
          colorScheme="blue"
          mr={3}
          onClick={() => {
            ding();
            onClose();
          }}
        >
          Yes
        </Button>
        <Button
          colorScheme="blue"
          mr={3}
          onClick={() => {
            ding();
            onClose();
          }}
        >
          No
        </Button>
      </ModalFooter>
      </ModalContent>
      </Modal>
    </>
  );
};

export default Exit;
