import axios from 'axios';
import React, { useState } from 'react';
import { Box, Button, ButtonGroup, Stack } from '@chakra-ui/react'
import { Flex } from '@chakra-ui/react'
import { Container } from '@chakra-ui/react'
import {
    FormControl,
    FormLabel,
    FormErrorMessage,
    FormHelperText,
    Input,
  } from '@chakra-ui/react'


  export default function Signup(){
    const [user,setuser]=useState({
      username :"",
      email:"",
      createpassword :"",
      confirmpassword:""
    })
    const handleClick=(e)=>{
      e.preventDefault();
      axios.post('/roboWar',user)
    }  

    return(
        <>
        <Container>
          <Flex color='white' align = "centre" >
            
                <Box w='650px' >
    <FormControl isRequired colorScheme='Red' align='centre'>
        <FormLabel> Username</FormLabel>
            <Input placeholder='Username' value={user.username} onChange={(event)=>setuser({...user,username:event.target.value})}/>
            <FormLabel>Email</FormLabel>
            <Input placeholder='Email' value={user.email} onChange={(event)=>setuser({...user,email:event.target.value})}/>
            <FormLabel>Create Password</FormLabel>
            <Input placeholder='Must include special symbols like @,# ' alue={user.createpassword} onChange={(event)=>setuser({...user,createpassword:event.target.value})}/>
            
            
            
            <FormLabel>Confirm Password</FormLabel>
            <Input placeholder='Confirm Password' alue={user.confirmpassword} onChange={(event)=>setuser({...user,confirmpassword:event.target.value})}/>
            

            
            

            
        </FormControl>
  </Box>
  
  
  
</Flex>
</Container>
        
        



        <Button colorScheme='teal' size='lg' margin={'5'}
        onClick={handleClick}
        >Signup</Button>


        
        </>
        )
    };
